modifier onlyAdmin() {
    require(admin == msg.sender, "A1" );
    _;
}

function getPBPvCHelperAddress() public view returns (address){
    return address(this);
}

function getTokensAdmin() external view onlyAdmin returns (uint, uint){
    return (winToken, lossToken);
}

